mapper.insert:
insert
into        usageCommutationTicket (account_id, startDate, endDate)
values      (#{account_id}, #{startDate}, #{endDate});

mapper.update:
update      usageCommutationTicket
set         endDate = #{endDate}
where       id = #{id};

mapper.select:
select      u.id, a.id, a.username, a.password, a.phoneNumber, a.point, a.remainTime, u.startDate, u.endDate
from        usageCommutationTicket as u
left join   account as a
on          u.account_id = a.id
where       a.id = #{id}
order by    u.endDate desc;
