mapper.insert:
insert
into        usageRoom (room_id, account_id, startDateTime, endDateTime)
values      (#{room_id}, #{account_id}, #{startDateTime}, #{endDateTime});

mapper.update:
update      usageRoom
set         room_id = #{room_id},
            account_id = #{account_id},
            startDateTime = #{startDateTime},
            endDateTime = #{endDateTime}
where       id = #{id};
